*,:after,:before{margin:0;padding:0;list-style-type:none;box-sizing:border-box}img{max-width:100%}ul{list-style:none}body{-webkit-font-smoothing:antialiased}.hello-world{line-height:1.5;text-align:center}.hello-world img{width:200px;height:200px;margin:0 auto 20px;opacity:0;display:block}.hello-world__subtitle,.hello-world__title{font-weight:300}.hello-world__title{font-size:70px}.hello-world__subtitle{font-size:30px}.hello-world ul{margin-top:20px}.hello-world ul li{margin-bottom:10px}.hello-world ul a{text-decoration:none}.site,body,html{min-height:100vh}.site{position:relative;background-color:#e2e2e5;box-shadow:0 0 40px 16px rgba(0,0,0,.22)}.site>*{position:absolute;top:50%;right:auto;bottom:auto;left:50%;transform:translate(-50%,-50%)}